// frontend/src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles/app.css";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");

  // Fetch tasks from backend
  const fetchTasks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  useEffect(() => {
    fetchTasks();
    const interval = setInterval(fetchTasks, 2000); // live updates
    return () => clearInterval(interval);
  }, []);

  // Add new task
  const addTask = async () => {
    if (!text.trim()) return;
    try {
      await axios.post("http://localhost:5000/tasks", { text });
      setText("");
      fetchTasks();
    } catch (err) {
      console.error("Error adding task:", err);
    }
  };

  // Toggle checkbox
  const toggleTask = async (id, person) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${id}`, { person });
      fetchTasks();
    } catch (err) {
      console.error("Error toggling task:", err);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  return (
    <div className="app-container">
      <h1>✅ Collaborative Tracker</h1>

      <div className="input-container">
        <input
          type="text"
          placeholder="Add a new task..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <div className="task-table">
        <div className="task-row header">
          <div>Person 1</div>
          <div>Task</div>
          <div>Person 2</div>
        </div>

        {tasks.map((task) => (
          <div key={task._id} className="task-row">
            <div>
              <input
                type="checkbox"
                checked={task.person1Done}
                onChange={() => toggleTask(task._id, "person1")}
              />
            </div>

            <div className="task-text">
              {task.text}{" "}
              <button className="delete-btn" onClick={() => deleteTask(task._id)}>
                ✖
              </button>
            </div>

            <div>
              <input
                type="checkbox"
                checked={task.person2Done}
                onChange={() => toggleTask(task._id, "person2")}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
