// frontend/src/App.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import TaskList from "./components/TaskList";
import CountdownTimer from "./components/CountdownTimer";
import "./styles/app.css";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");

  // Fetch tasks from backend
  const fetchTasks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
    }
  };

  useEffect(() => {
    fetchTasks();
    const interval = setInterval(fetchTasks, 2000); // live updates every 2s
    return () => clearInterval(interval);
  }, []);

  // Add new task
  const addTask = async () => {
    if (!text.trim()) return;
    try {
      await axios.post("http://localhost:5000/tasks", { text });
      setText("");
      fetchTasks();
    } catch (err) {
      console.error("Error adding task:", err);
      alert("Failed to add task. Make sure backend is running.");
    }
  };

  // Toggle checkbox
  const toggleTask = async (id, person) => {
    try {
      await axios.put(`http://localhost:5000/tasks/${id}`, { person });
      fetchTasks();
    } catch (err) {
      console.error("Error toggling task:", err);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  return (
    <div className="app-container">
      {/* Countdown timer top-right */}
      <CountdownTimer />

      <h1>âœ… Collaborative Tracker</h1>

      <div className="input-container">
        <input
          type="text"
          placeholder="Add a new task..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <TaskList tasks={tasks} toggleTask={toggleTask} deleteTask={deleteTask} />
    </div>
  );
}
