// frontend/src/components/TaskList.jsx
import React from "react";

export default function TaskList({ tasks, toggleTask, deleteTask }) {
  const usernames = ["Apple", "Banana"]; // display names for columns

  return (
    <div className="task-table">
      {/* Header */}
      <div className="task-row header">
        <div>{usernames[0]}</div>
        <div>Task</div>
        <div>{usernames[1]}</div>
      </div>

      {/* Task rows */}
      {tasks.map((task) => (
        <div key={task._id} className="task-row">
          {/* Checkbox for first user */}
          <div>
            <input
              type="checkbox"
              checked={task[usernames[0] + "Done"]}
              onChange={() => toggleTask(task._id, usernames[0])}
            />
          </div>

          {/* Task text + delete button */}
          <div className="task-text">
            {task.text}
            <button className="delete-btn" onClick={() => deleteTask(task._id)}>
              ‚ùå
            </button>
          </div>

          {/* Checkbox for second user */}
          <div>
            <input
              type="checkbox"
              checked={task[usernames[1] + "Done"]}
              onChange={() => toggleTask(task._id, usernames[1])}
            />
          </div>
        </div>
      ))}
    </div>
  );
}
