import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles/app.css";

export default function App() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");

  const fetchTasks = async () => {
    const res = await axios.get("http://localhost:5000/tasks");
    setTasks(res.data);
  };

  useEffect(() => {
    fetchTasks();
    const interval = setInterval(fetchTasks, 2000); // live updates
    return () => clearInterval(interval);
  }, []);

  const addTask = async () => {
    if (!text.trim()) return;
    await axios.post("http://localhost:5000/tasks", { text });
    setText("");
    fetchTasks();
  };

  const toggleTask = async (id, person) => {
    await axios.put(`http://localhost:5000/tasks/${id}`, { person });
    fetchTasks();
  };

  const deleteTask = async (id) => {
    await axios.delete(`http://localhost:5000/tasks/${id}`);
    fetchTasks();
  };

  return (
    <div className="app-container">
      <h1>âœ… Collaborative Tracker</h1>

      <div className="input-container">
        <input
          type="text"
          placeholder="Add a new task..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <div className="task-table">
        <div className="task-row header">
          <div>Person 1</div>
          <div>Task</div>
          <div>Person 2</div>
        </div>
        {tasks.map((task) => (
          <div key={task.id} className="task-row">
            <div>
              <input
                type="checkbox"
                checked={task.person1Done}
                onChange={() => toggleTask(task.id, "person1")}
              />
            </div>
            <div className="task-text">{task.text}</div>
            <div>
              <input
                type="checkbox"
                checked={task.person2Done}
                onChange={() => toggleTask(task.id, "person2")}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
